# ----------------------------------------------------------------
# Keybinds
# ----------------------------------------------------------------

# Load video in mpv
config.bind(',v', 'spawn -d mpv {url}')
config.bind(',m', 'spawn -d mpv --no-video {url}')
config.bind(',V', 'hint links spawn -d mpv {hint-url}')
config.bind(',I', 'hint links spawn -d feh {hint-url}')
config.bind(',f', 'spawn -d feh {url}')

# Download video with youtube-dl
config.bind(',d', 'spawn -d youtube-dl {url}')
config.bind(',D', 'hint links spawn -d youtube-dl -c -o ~/Videos/%(title)s.%(ext)s {hint-url}')
config.bind(',lD', 'hint links spawn -d youtube-dl -c -f "bestvideo[height<=720]+bestaudio/best[height<=720]" -o ~/Videos/%(title)s.%(ext)s {hint-url}')
config.bind(',S', 'hint links spawn -d youtube-dl -c -x -o ~/Sounds/%(title)s.%(ext)s {hint-url}')
# Download a torrent mirror
config.bind(',tm', 'hint links spawn deluge-console add {hint-url}')

# Download a git page
config.bind(',git', 'spawn git clone {url}')

# Download an imgur album
config.bind(',img', 'spawn python3 ~/Scripts/imguralbum.py {url}')

config.bind(';D', 'hint images download')

